import './Report.css';
import { useState } from 'react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';


export default function Report({ reports, setReports}) {
    const [add, setAdd] = useState(false);
    const [text, setText] = useState(null);

    const stripHtmlTags = (html) => {
        const doc = new DOMParser().parseFromString(html, 'text/html');
        return doc.body.textContent || "";
      };

    const handleAdd = () => {
        setAdd(true);
    }

    const handlePost = () => {
        setReports((prevReports) => [...prevReports, text]);
        setAdd(false);    
        setText()
    }

    return (
        <>
        <div className='container'>
            <h1 className='report-header'>Report</h1>
            <button onClick={handleAdd}>+ New Report</button>
            {add && 
                <>
                <ReactQuill className='text-editor'
                    value={text}
                    onChange={value => setText(value)}
                />
                <button onClick={handlePost}>Post</button>
                </>
            }
            <ul>
                {reports.map((report, index) => (
                    <li key={index}>{stripHtmlTags(report)}</li>
                ))}
            </ul>
        </div>
        </>
    );
};
