import './Report.css';
import { useState } from 'react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';


export default function Report({ reports, setReports}) {
    const [add, setAdd] = useState(false);
    const [text, setText] = useState(null);
    const [date, setDate] = useState(new Date());

    const handleAdd = () => {
        setAdd(true);
    }

    const handlePost = () => {
        setReports((prevReports) => [{text, date}, ...prevReports]);
        setAdd(false);    
        setText(null)
    }

    return (
        <>
        <div className='container'>
            <h1 className='report-header'>Report</h1>
            <button onClick={handleAdd}>+ New Report</button>
            <div className='text-editor'>
                {add && 
                    <>
                    <ReactQuill
                        value={text}
                        onChange={value => setText(value)}
                    />
                    <button onClick={handlePost}>Post</button>
                    </>
                }
                <ul>
  {reports.map((report, index) => (
    <li className='reports' key={index}>
      <div>
        <p dangerouslySetInnerHTML={{ __html: report.text }} />
        <small>{report.date}</small>
      </div>
    </li>
  ))}
</ul>
            </div>
        </div>
        </>
    );
};
